'use strict';

import * as WEMath from 'WEMath';

// This will be used to separate each element
let delimiter = ':';

// Whether you want to see the seconds or not
let showSeconds = true;

// Whether you want a 24h or 12h style format
let use24hFormat = true;

/**
 * @param {String} value (for property 'text')
 */
var shadowLayer;
export function update(value) {
	shadowLayer.visible = thisLayer.visible;
	let time = new Date();
	var hours;
	var suffix = '';
	if (use24hFormat) {
		hours = ("00" + time.getHours()).slice(-2);
	} else {
		hours = time.getHours();
		suffix = hours >= 12 ? 'PM' : 'AM';
		hours %= 12;
		if (hours == 0) {
			hours = 12;
		}
	}
	let minutes = ("00" + time.getMinutes()).slice(-2);
	let seconds = ("00" + time.getSeconds()).slice(-2);
	value = hours + delimiter + minutes;
	if (showSeconds) {
		value += delimiter + seconds;
	}
	if (!use24hFormat) {
		value += ' ' + suffix;
	}

	var delta = thisLayer.origin.subtract(input.cursorWorldPosition);
	var shadowOffset = delta.copy();
	shadowOffset.z = 0;
	delta = delta.divide(new Vec3(engine.canvasSize, 1));
	delta.z = 0;
	var rotation = new Vec3(delta.y, -delta.x, 4 * WEMath.mix(delta.x, -delta.x, Math.min(1, Math.max(0, delta.y * 0.1 + 0.5)))).multiply(50);
	thisLayer.angles = rotation;
	shadowLayer.angles = rotation;
	shadowLayer.origin = thisLayer.origin.add(shadowOffset.multiply(0.03));
	shadowLayer.text = value;
	return value;
}

/**
 * @param {String} value (for property 'text')
 */
export function init() {
	shadowLayer = thisScene.createLayer({
		text: 'shadow',
		color: '0 0 0',
		alpha: 1,
		pointsize: thisLayer.pointsize,
		font: thisLayer.font,
		perspective: true
	});
	shadowLayer.origin = thisLayer.origin;
	shadowLayer.angles = thisLayer.angles;
	shadowLayer.scale = thisLayer.scale;
	thisScene.sortLayer(shadowLayer, thisScene.getLayerIndex(thisLayer));
}
